    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        // TODO add your handling code here:
        
        double empWorkHours, hourPrice, reteValue, totalSalary, subtotalSalary, reteTotal;
        int empCod;
        String empName;
        
        empWorkHours = Double.parseDouble(txtWorkHours.getText());
        hourPrice = Double.parseDouble(txtHourValue.getText());
        reteValue = Double.parseDouble(txtRete.getText());
        
        Double perRete = reteValue / 100;
        
        subtotalSalary = empWorkHours * hourPrice;
        reteTotal = subtotalSalary * perRete;
        totalSalary = subtotalSalary - reteTotal;
        
        txtSubSal.setText(String.valueOf(subtotalSalary));
        txtTotalSal.setText(String.valueOf(totalSalary));
        
        
    }//GEN-LAST:event_btnCalculateActionPerformed

